/**
 *
 * @class Base
 * @description 组件的基类
 */
import CONSTANT from './constant'
class Base{
    _beforeCreate(beforeCreate=null){
        if(beforeCreate && typeof beforeCreate == CONSTANT.ISFUNCTION){
            beforeCreate();
        }else if(typeof this.config.beforeCreate == CONSTANT.ISFUNCTION){
            this.config.beforeCreate();
        }
    }
    _create(create=null){
        if(create && typeof create == CONSTANT.ISFUNCTION){
            create();
        }else if(typeof this.config.create == CONSTANT.ISFUNCTION){
            this.config.create();
        }
    }
    _beforeUpdate(beforeUpdate=null){
        if(beforeUpdate && typeof beforeUpdate == CONSTANT.ISFUNCTION){
            beforeUpdate();
        }else if(typeof this.config.beforeUpdate == CONSTANT.ISFUNCTION){
            this.config.beforeUpdate();
        }
    }
    _update(update=null){
        if(update && typeof update == CONSTANT.ISFUNCTION){
            update();
        }else if(typeof this.config.update == CONSTANT.ISFUNCTION){
            this.config.update();
        }
    }
    _beforeDestroy(beforeDestroy=null){
        if(beforeDestroy && typeof beforeDestroy == CONSTANT.ISFUNCTION){
            beforeDestroy();
        }else if(typeof this.config.beforeDestroy == CONSTANT.ISFUNCTION){
            this.config.beforeDestroy();
        }
    }
    _destory(destory=null){
        if(destory && typeof destory == CONSTANT.ISFUNCTION){
            destory();
        }else if(typeof this.config.destory == CONSTANT.ISFUNCTION){
            this.config.destory();
        }
    }
	_ISIE8() {
		if (navigator.appName == "Microsoft Internet Explorer" && navigator.appVersion.match(/8./i) == "8.") {
			return true;
		}
		return false;
	}
}
export default Base;